import React, { useState } from 'react'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

/**
 * ImageWithFallback component displays an image with automatic fallback to a placeholder on error.
 * Useful for handling broken image URLs gracefully in the UI.
 */
export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  return (
    <img
      {...props}
      src={didError ? ERROR_IMG_SRC : props.src}
      onError={handleError}
    />
  )
}